INSERT INTO `categories` (`category_id`, `name`, `description`) VALUES
(0, 'Žiadna', ''),
(1, 'League of Legends', 'League of Legends je online multiplayerová hra typu battle arena (MOBA), vyvinutá a publikovaná spoločnosťou Riot Games. Hráči si vyberajú postavy nazývané \"šampióni\" a zúčastňujú sa tímových súbojov proti ostatným hráčom alebo umeléj inteligencii. Cieľom je zničiť nepriateľskú základňu, tzv. Nexus, pričom hráči zbierajú skúsenosti a získavajú hernú menu, ktorou môžu investovať do vylepšení svojich postáv. Hra je známa pre svoju strategickú hĺbku, rôznorodosť šampiónov a taktické možnosti v priebehu bitiek, čo ju robí jednou z najobľúbenejších hier vo svete e-sportu i medzi casual hráčmi.'),
(2, 'Counter-Strike 2\r\n\r\n', '\"Counter-Strike 2\" je pokračovanie populárnej herné série \"Counter-Strike\", vyvinutej spoločnosťou Valve Corporation. Ide o multiplayerovú first-person shooting hru (FPS), kde sa hráči rozdeľujú do tímov teroristov a protiteroristov a bojujú medzi sebou v rôznych misiách a režimoch hry. Hra je známa pre svoju taktickú hrateľnosť, realistické zbrane a dynamické herné prostredie, čo ju robí obľúbenou vo svete e-sportu aj medzi casual hráčmi.'),
(3, 'The Witcher 3: Wild Hunt', 'Epický RPG príbeh, ktorý sleduje dobrodružstvá Geralta z Rivie. Hráči prechádzajú nádherný otvorený svet a plnia rôzne úlohy.'),
(4, 'Red Dead Redemption 2', 'Príbeh Arthura Morgana a jeho gangu v divokom západe. Hra ponúka obrovský otvorený svet a hlbokú príbehovú líniu.'),
(5, 'The Legend of Zelda: Breath of the Wild', 'Inovatívna hra, ktorá redefinovala otvorené svety. Hráči preskúmavajú Hyrule a riešia rôzne hádanky a úlohy.'),
(6, 'Cyberpunk 2077', 'Futuristický otvorený svet v meste Night City. Hráči si môžu prispôsobiť svoju postavu a plniť rôzne misie.'),
(7, 'God of War', 'Reboot série God of War, kde Kratos a jeho syn Atreus prechádzajú svet severskej mytológie.'),
(8, 'Minecraft', 'Kreatívna hra, kde hráči môžu stavať a preskúmavať nekonečný blokový svet.'),
(9, 'Fortnite', 'Populárna battle royale hra s farebným grafickým štýlom a staviteľskými prvkami.'),
(10, 'Overwatch', 'Tímová strieľačka s rôznymi hrdinami, každý s unikátnymi schopnosťami. Hráči spolupracujú, aby dosiahli ciele v rôznych herných módoch.'),
(11, 'Grand Theft Auto V', 'Otvorený svet, kde hráči sledujú príbeh troch rôznych postáv a zapájajú sa do rôznych kriminálnych aktivít v meste Los Santos.'),
(12, 'Assassin’s Creed Odyssey', 'Hra zasadená do starovekého Grécka, kde hráči preberajú rolu Alexiosa alebo Kassandry a preskúmavajú otvorený svet plný historických udalostí a mytológií.'),
(13, 'The Elder Scrolls V: Skyrim', 'Otvorený svet plný drakov, kde hráči môžu preskúmať obrovské krajiny a zapojiť sa do rôznych questov.'),
(14, 'Horizon Zero Dawn', 'Postapokalyptická hra, kde hráči sledujú príbeh Aloy, lovkyne bojujúcej proti mechanickým tvorom v otvorenom svete.'),
(15, 'Persona 5', 'JRPG, kde hráči sledujú skupinu študentov, ktorí sa stávajú zlodejmi duší a bojujú proti zlu v alternatívnom svete.'),
(16, 'Dark Souls III', 'Náročná akčná RPG hra s temným a atmosférickým svetom, kde hráči čelia silným nepriateľom a bossom.'),
(17, 'Celeste', 'Platformová hra, kde hráči pomáhajú hlavnému hrdinovi prekonať emocionálne a fyzické prekážky pri lezení na horu Celeste.'),
(18, 'Stardew Valley', 'Farmárska simulácia, kde hráči obnovujú starú farmu, pestujú plodiny, chovajú zvieratá a spoznávajú miestnych obyvateľov.'),
(19, 'Doom (2016)', 'Rýchla akčná strieľačka, kde hráči bojujú proti démonom z pekla na Marsu.'),
(20, 'The Last of Us Part II', 'Emocionálny príbeh o prežití v postapokalyptickom svete, kde hráči sledujú osudy Ellie a Joela.'),
(21, 'Spider-Man', 'Akčná hra, kde hráči preberajú rolu Spider-Mana a bojujú proti zločinu v New Yorku.'),
(22, 'Sekiro: Shadows Die Twice', 'Akčná hra z prostredia feudalného Japonska, kde hráči ovládajú shinobiho a bojujú proti rôznym nepriateľom a bossom.');

